        // </div>
        <div className="w-full bg-themegreen  glass-green rounded-2xl shadow-md p-6 flex flex-col md:flex-row gap-6">
      {/* Image Section */}
      <div className="w-full md:w-1/3">
        <img
          src="https://via.placeholder.com/400x250"
          alt="Blog"
          className="w-full h-full object-cover rounded-xl"
        />
      </div>

      {/* Text Section */}
      <div className="w-full md:w-2/3 flex flex-col justify-between">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Blog Title</h2>
        <p className="text-gray-600">
          This is a short description of the blog post. It gives a quick overview of the content and entices the reader to click and read more.
        </p>
      </div>
    </div>
    )
}

export default Blog_card;
