import menu from "./images/open-menu.png"
import logo from "./images/stocklogo.png"
import { useState } from "react";
import "./style.css"
// import { NavLink } from "react-router";
import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom";

function Nav() {

  const [show, showhandler] = useState(false);
  const navHandler = () => {
    if (show == false) {
      showhandler(true);
    }
    if (show == true) {
      showhandler(false);
    }
  }
  const navg = useNavigate();

  return (
    <div className=" relative flex justify-center  ">

      <div className=" relative h-16 md:h-20 bg-themeblack glass-green2 flex justify-center  z-50 items-center   lg:fixed mt-4">
        <div className="flex flex-row w-full px-0 md:mx-4">
          <div className=" md:w-1/3 md:p-3 " onClick={() => { navg("/") }}>
            <img className=" max-w-xs pl-0 p-32 md:w-2/3" src={logo} />
          </div>
           <div className=" w-full xl:hidden relative flex items-center">
            <img className="w-10 h-8" onClick={navHandler} src={menu} />

          </div>
          <div className="w-2/3 flex flex-row justify-end items-center gap-20 px-10 text-textwhite font-semibold invisible  lg:visible">
            <Link to="/courses"> <div className=" flex items-center text-xl">Courses</div></Link>
            {/* <Link to="/blog"><div className=" flex items-center text-xl" >Performance</div></Link> */}
            <Link to="/about_us"><div className=" flex items-center text-xl">About us</div></Link>

            <Link to="/contact"> <div className=" flex items-center text-xl">Contact us</div></Link>
          </div>


        </div>
      </div>
      <div className={`w-full h-auto absolute ${show ? "mt-0" : "-mt-40"} z-0 transition-all duration-300 p-5 bg-primary opacity-50 text-white font-bold font-sans flex flex-col items-center`}>
        <div>Courses</div>
        <div>Services</div>
        <div>Blogs</div>
        <div>About us</div>
        <div>Login</div>
        <div>Sign in</div>
      </div>
    </div>
  );
}

export default Nav;
