import menu from "./images/open-menu.png"
import logo from "./images/logo_about.png"
import { useState } from "react";
function Nav() {

const[ show, showhandler] = useState(false);
  const navHandler= ()=>{
   if(show == false){
    showhandler(true);
   }
   if(show == true){
    showhandler(false);
   }
  }
  return (
    <div className=" relative">
    <div className="w-full relative h-16 md:h-20 bg-themeblack drop-shadow-md flex justify-center  z-50 items-center   lg:fixed  ">
      <div className="flex flex-row w-full px-3 md:mx-12 ">
        <div className="w-1/3  ">
          <img className="w-20" src={logo} />
        </div>
        <div className="w-2/3 flex flex-row justify-end gap-20 text-themegreen font-semibold invisible  lg:visible">
          <div className=" flex items-center text-xl">Courses</div>
          <div className=" flex items-center text-xl">Services</div>
          <div className=" flex items-center text-xl">Blogs</div>
          <div className=" flex items-center text-xl">About us</div>
        </div>

        <div className="xl:invisible relative flex items-center">
          <img className="w-10 h-8" onClick={navHandler} src={menu} />

        </div>
      </div>
    </div>
    <div className= {`w-full h-auto absolute ${show? "mt-0": "-mt-40"} z-0 transition-all duration-300 p-5 bg-primary opacity-50 text-white font-bold font-sans flex flex-col items-center`}>
    <div>Courses</div>
    <div>Services</div>
    <div>Blogs</div>
    <div>About us</div>
    <div>Login</div>
    <div>Sign in</div>
  </div>
  </div>
  );
}

export default Nav;
